import React, { useState } from 'react';
import ItemsCarousel from 'react-items-carousel';

import '../Css/BlogCaroussel.css'

function Test() {
  const [activeItemIndex, setActiveItemIndex] = useState(0);
  const chevronWidth = 40;
  return (
    <div style={{ padding: `0 ${chevronWidth}px` }}>
      <ItemsCarousel
        requestToChangeActive={setActiveItemIndex}
        activeItemIndex={activeItemIndex}
        numberOfCards={2}
        gutter={20}
        leftChevron={<button>{'<'}</button>}
        rightChevron={<button>{'>'}</button>}
        outsideChevron
        chevronWidth={chevronWidth}
      >
        <div style={{ height: 400, background: '#EEE' }}>
                <div className='card'>
                    <div className='card1-pic'></div>
                    <div className='card1-desc'>
                        <p>Publié le 21.04.2020</p>
                        <h3>Petit dejeuné en bord de mer au levé du jour</h3>
                         <div className='center-btn-in-card'><div className='card-btn'>En savoir plus</div></div>
                    </div>
                </div>
          </div>
        <div style={{ height: 300, background: '#EEE' }}>Second card</div>
        <div style={{ height: 300, background: '#EEE' }}>Third card</div>
        <div style={{ height: 300, background: '#EEE' }}>Fourth card</div>
      </ItemsCarousel>
    </div>
  );
};

export default Test
